#!/bin/bash\n\n# Script de Deploy Automático para GitHub Pages\n# Jarvis Web - João Manoel\n\necho \"🚀 Iniciando deploy do Jarvis Web para GitHub Pages...\"\necho \"\"\n\n# Verificar se estamos no diretório correto\nif [ ! -d \"docs\" ]; then\n    echo \"❌ Erro: Pasta 'docs' não encontrada!\"\n    echo \"Execute este script na raiz do projeto jarvis.\"\n    exit 1\nfi\n\n# Verificar se git está inicializado\nif [ ! -d \".git\" ]; then\n    echo \"❌ Erro: Repositório git não encontrado!\"\n    echo \"Inicialize o git primeiro com: git init\"\n    exit 1\nfi\n\n# Verificar status do git\necho \"📋 Verificando status do repositório...\"\ngit status\n\necho \"\"\necho \"📦 Adicionando arquivos do Jarvis Web...\"\ngit add docs/\ngit add DEPLOY_GITHUB_PAGES.md\ngit add deploy-github-pages.sh\n\n# Verificar se há mudanças para commit\nif git diff --staged --quiet; then\n    echo \"ℹ️ Nenhuma mudança detectada nos arquivos do Jarvis Web.\"\nelse\n    echo \"💾 Fazendo commit das mudanças...\"\n    git commit -m \"🤖 Deploy Jarvis Web para GitHub Pages\n    \n    - Interface web completa com reconhecimento de voz\n    - Integração com Google Gemini AI\n    - PWA com funcionalidade offline\n    - Design responsivo mantendo estilo original\n    - Configurações persistentes no localStorage\n    \n    URL: https://joaomanoel123.github.io/jarvis\"\nfi\n\necho \"\"\necho \"🌐 Enviando para GitHub...\"\ngit push origin main\n\nif [ $? -eq 0 ]; then\n    echo \"\"\n    echo \"✅ Deploy realizado com sucesso!\"\n    echo \"\"\n    echo \"📋 Próximos passos:\"\n    echo \"1. Acesse: https://github.com/joaomanoel123/jarvis/settings/pages\"\n    echo \"2. Configure:\"\n    echo \"   - Source: Deploy from a branch\"\n    echo \"   - Branch: main\"\n    echo \"   - Folder: /docs\"\n    echo \"3. Clique em 'Save'\"\n    echo \"\"\n    echo \"🌟 Seu Jarvis Web estará disponível em:\"\n    echo \"   https://joaomanoel123.github.io/jarvis\"\n    echo \"\"\n    echo \"⏱️ Aguarde alguns minutos para o GitHub processar o deploy.\"\n    echo \"\"\n    echo \"🔑 Não esqueça de configurar sua chave API do Google Gemini!\"\n    echo \"   Obtenha em: https://makersuite.google.com/app/apikey\"\nelse\n    echo \"\"\n    echo \"❌ Erro no push para GitHub!\"\n    echo \"Verifique sua conexão e configurações do git.\"\n    exit 1\nfi\n\necho \"\"\necho \"🎉 Deploy concluído! Seu assistente virtual está pronto para uso!\""