#!/usr/bin/bash\n\n# 🚀 Deploy Completo do Jarvis - Todas as Melhorias\n# Script para fazer deploy de todas as funcionalidades implementadas\n\necho \"🤖 ===========================================\"\necho \"🚀  JARVIS DEPLOY COMPLETO - TODAS AS MELHORIAS\"\necho \"🤖 ===========================================\"\necho \"\"\n\n# Verificar se estamos no diretório correto\nif [ ! -f \"docs/index.html\" ]; then\n    echo \"❌ Erro: Execute este script na raiz do projeto Jarvis\"\n    exit 1\nfi\n\necho \"✅ Verificações iniciais concluídas\"\necho \"\"\n\n# Mostrar status atual\necho \"📊 Status atual do repositório:\"\ngit status --short\necho \"\"\n\n# Verificar se há mudanças para commitar\nif git diff --quiet && git diff --cached --quiet; then\n    echo \"ℹ️  Nenhuma mudança detectada para commit\"\n    echo \"📡 Verificando se há commits para push...\"\n    \n    # Verificar se há commits para push\n    if git log origin/main..HEAD --oneline | grep -q .; then\n        echo \"📤 Há commits locais para push\"\n    else\n        echo \"✅ Repositório já está sincronizado\"\n        echo \"🌐 Jarvis disponível em: https://joaomanoel123.github.io/jarvis\"\n        exit 0\n    fi\nelse\n    echo \"📁 Adicionando todas as mudanças...\"\n    git add .\n    \n    echo \"✅ Arquivos adicionados\"\n    echo \"\"\n    \n    # Verificar mudanças\n    echo \"📋 Mudanças a serem commitadas:\"\n    git diff --cached --name-only\n    echo \"\"\n    \n    # Confirmar com o usuário\n    read -p \"🤔 Deseja continuar com o deploy? (y/N): \" -n 1 -r\n    echo \"\"\n    if [[ ! $REPLY =~ ^[Yy]$ ]]; then\n        echo \"❌ Deploy cancelado pelo usuário\"\n        exit 1\n    fi\n    \n    # Fazer commit\n    echo \"💾 Fazendo commit...\"\n    COMMIT_MSG=\"🚀 Deploy completo - Jarvis com todas as melhorias\n\n✨ Funcionalidades implementadas:\n- 🗣️ Text-to-Speech cristalino (português brasileiro)\n- 🎤 Reconhecimento de voz avançado (Web Speech API)\n- 🛡️ Diálogos de permissão para sites externos\n- 📱 Interface mobile 100% responsiva\n- 👆 Gestos intuitivos (swipe up, duplo toque)\n- 🔧 Diagnóstico completo de áudio\n- 🎮 Controles avançados integrados\n- 🌐 Compatibilidade total com GitHub Pages\n- 🚀 Performance otimizada\n- 💾 Configurações persistentes\n\n🎯 Deploy: $(date '+%Y-%m-%d %H:%M:%S')\"\n    \n    git commit -m \"$COMMIT_MSG\"\n    \n    if [ $? -eq 0 ]; then\n        echo \"✅ Commit realizado com sucesso\"\n    else\n        echo \"❌ Erro no commit\"\n        exit 1\n    fi\nfi\n\necho \"\"\n\n# Push para o repositório\necho \"🚀 Fazendo push para GitHub...\"\ngit push origin main\n\nif [ $? -eq 0 ]; then\n    echo \"✅ Push realizado com sucesso!\"\nelse\n    echo \"❌ Erro no push\"\n    exit 1\nfi\n\necho \"\"\necho \"🎉 ===========================================\"\necho \"🚀  DEPLOY CONCLUÍDO COM SUCESSO!\"\necho \"🎉 ===========================================\"\necho \"\"\necho \"🌐 Seu Jarvis completo estará disponível em:\"\necho \"   https://joaomanoel123.github.io/jarvis\"\necho \"\"\necho \"🧪 Páginas de teste:\"\necho \"   • TTS: https://joaomanoel123.github.io/jarvis/test-tts-integration.html\"\necho \"\"\necho \"⏱️  Aguarde 2-5 minutos para o GitHub Pages atualizar\"\necho \"\"\necho \"🎯 FUNCIONALIDADES ATIVAS:\"\necho \"   ✅ 🗣️ Voz cristalina em português brasileiro\"\necho \"   ✅ 🎤 Reconhecimento de voz avançado\"\necho \"   ✅ 🛡️ Diálogos de segurança para sites\"\necho \"   ✅ 📱 Interface mobile responsiva\"\necho \"   ✅ 👆 Gestos intuitivos (swipe, duplo toque)\"\necho \"   ✅ 🔧 Diagnóstico de áudio integrado\"\necho \"   ✅ 🎮 Controles avançados\"\necho \"   ✅ 🌐 100% compatível GitHub Pages\"\necho \"   ✅ 🚀 Performance otimizada\"\necho \"\"\necho \"🎮 COMO USAR:\"\necho \"   • 🎤 Clique no microfone e fale\"\necho \"   • ⚙️ Acesse configurações para ajustar voz\"\necho \"   • 📱 Em mobile: swipe up (chat) ou duplo toque (mic)\"\necho \"   • 🗣️ Digite comandos como 'whatsapp', 'youtube'\"\necho \"\"\necho \"🤖 SEU JARVIS ESTÁ COMPLETO E FUNCIONANDO! 🎉\"\necho \"\""